/***************** Lazy Loader *********************************************
    lazyLoad(px height to trigger full load on)
*******************************************************************************/
function lazyLoad(heightTrigger) {
    function imgInit(all) {
        if (all) {
            $(".lazyImg").each(function() {
                $(this).attr("src", $(this).attr("data-original"));
            });
        } else {
            $(".lazyImg").filter(function() {
                return $(this).offset().top <= heightTrigger;
            }).each(function() {
                $(this).attr("src", $(this).attr("data-original"));
            });
        }
    }

    function onScrollOrResize() {
        if ($(window).scrollTop() + $(window).height() >= heightTrigger) {
            imgInit(true);

            $(window).unbind('scroll', onScrollOrResize);
            $(window).unbind('resize', onScrollOrResize);
        }
    }

    if ($(window).scrollTop() + $(window).height() >= heightTrigger) {
        imgInit(true);
    } else {
        imgInit(false);
        $(window).scroll(onScrollOrResize).resize(onScrollOrResize);
    }
}

$(document).ready(function() {
    lazyLoad(400);
});